var exit = properties.get('exitCode');
var beginCheck = 0;
properties.put('FoundNewRequest', 'False');

scanner.register("${p:REPO_ROOT}", function(lineNumber, line) {
    properties.put("FoundNewRequest", 'True');
});

if (exit == 0) {
   properties.put('Status', 'Success');
   scanner.scan();
}

else {
   properties.put('Status', 'Failure');
}

var exit = properties.get('exitCode');
var beginCheck = 0;
properties.put('Exists', 'False');

scanner.register("Filename", function(lineNumber, line) {
    var filename = line.substring(line.lastIndexOf('=')+1).trim();
    // The '=' is the symbol after which the template name value starts.
    properties.put("ReqFilename", filename);
});

if (exit == 0) {
   properties.put('Status', 'Success');
   scanner.scan();
}

else {
   properties.put('Status', 'Failure');
}
