var exit = properties.get('exitCode');
var beginCheck = 0;
properties.put('Exists', 'False');

scanner.register("TargetDomain", function(lineNumber, line) {
    var envName = line.substring(line.lastIndexOf('=')+1).trim();
    // The '=' is the symbol after which the environment name value starts.
    properties.put("TargetEnvironment", envName);
});

if (exit == 0) {
   properties.put('Status', 'Success');
   scanner.scan();
}

else {
   properties.put('Status', 'Failure');
}

