postProcessingScript.body:
var exit = properties.get('exitCode');
var beginCheck = 0;
properties.put('Exists', 'False');
scanner.register("Output text:", function(lineNumber, line) {
beginCheck = 1;
});
scanner.register("GENERATE PSB=", function(lineNumber, line) {
if (beginCheck == 1) {
properties.put('Exists', 'True');
}
});
if (exit == 0) {
properties.put('Status', 'Success');
scanner.scan();
}
else {
properties.put('Status', 'Failure');
}

